import React from "react";
import "./App.css";
import Axios from "axios";

export default function App() {
  const [values, setValues] = React.useState();
  const [listaPessoas, setListaPessoas] = React.useState();

  React.useEffect(() => { 
    Axios.get("http://localhost:3001/listar")
      .then(res => {
        setListaPessoas(res.data);
      })
  }, [])

  const handleChangeValues = (values) => {
    setValues((prevValue) => ({
      ...prevValue,
      [values.target.name]: values.target.value,
    }));
  };

  const addPessoa = () => {
    Axios.post("http://localhost:3001/criar", {
      nome: values.nome,
      cpf: values.cpf,
      idade: values.idade,
    }).then((response) => {
      console.log(response);
    });
  };

  const listarPessoas = () => {
    Axios.get("http://localhost:3001/listar").then((response) => {
      console.log(response);
    });
  };

  return (
    <div className="app-container">
      <div className="register-container">
        <h1 className="register-title">Cadastro de pessoas</h1>

        <input
          type="text"
          name="nome"
          placeholder="Nome"
          className="register-input"
          onChange={handleChangeValues}
        />
        <input
          type="text"
          placeholder="CPF"
          name="cpf"
          className="register-input"
          onChange={handleChangeValues}
        />
        <input
          type="text"
          placeholder="Idade"
          name="idade"
          className="register-input"
          onChange={handleChangeValues}
        />

        <button className="register-button" onClick={() => addPessoa()}>
          {" "}
          Cadastrar{" "}
        </button>
      </div>

      <button onClick={() => listarPessoas()}>Listar</button>

      {listaPessoas && listaPessoas.map((pessoa) => {
        return (
          <div className="list-container">
            <p>{pessoa.nome}</p>
            <p>{pessoa.cpf}</p>
            <p>{pessoa.idade}</p>
          </div>
        )
        })}
    </div>
  );
}
