import React from "react";
import "./App.css";
import Axios from "axios";
import Card from "./components/cards/cards";

export default function App() {
  const [values, setValues] = React.useState();
  const [listaPessoas, setListaPessoas] = React.useState([]);

  console.log(listaPessoas);

  React.useEffect(() => {
    Axios.get("http://localhost:3001/listar").then((res) => {
      setListaPessoas(res.data);
    });
  }, []);

  const handleChangeValues = (values) => {
    setValues((prevValue) => ({
      ...prevValue,
      [values.target.name]: values.target.value,
    }));
  };

  const addPessoa = () => {
    Axios.post("http://localhost:3001/criar", {
      nome: values.nome,
      cpf: values.cpf,
      idade: values.idade,
    }).then((response) => {
      setListaPessoas([...listaPessoas, {
        id: response.data[0].id,
        nome: values.nome,
        cpf: values.cpf,
        idade: values.idade,
      }]);
    });
  };

  return (
    <div className="app-container">
      <div className="register-container">
        <h1 className="register-title">Cadastro de pessoas</h1>

        <input
          type="text"
          name="nome"
          placeholder="Nome"
          className="register-input"
          onChange={handleChangeValues}
        />
        <input
          type="text"
          placeholder="CPF"
          name="cpf"
          className="register-input"
          onChange={handleChangeValues}
        />
        <input
          type="text"
          placeholder="Idade"
          name="idade"
          className="register-input"
          onChange={handleChangeValues}
        />

        <button className="register-button" onClick={() => addPessoa()}>
          Cadastrar
        </button>
      </div>

      {listaPessoas &&
        listaPessoas.map((pessoa) => {
          <h3></h3>
          // <Card
          //   listaPessoas={listaPessoas}
          //   setListaPessoas={setListaPessoas}
          //   key={pessoa.id}
          //   id={pessoa.id}
          //   nome={pessoa.nome}
          //   cpf={pessoa.cpf}
          //   idade={pessoa.idade}
          // />;
        })}
    </div>
  );
}
